(function($){$.fn.ignite=function(burn){$(this).each(function(){var letters=$(this).text().split("");$(this).html("<span>"+letters.join("</span><span>")+"</span>");$spans=$(this).find("span");setInterval(function(){$spans.each(burn)},100)})}})(jQuery);function gasFlame(){var colors=["rgba(45,45,45,0.5)","rgba(43, 161, 61, 0.35)","rgba(43, 161, 61, 0.65)","#fff"];if(Math.random()>0.9){colors.push("#f90")}
var hv=0.06;var textShadows=[];var c=0;var h=0;var s=0;while(c<colors.length){s=1+Math.round(Math.random()*0.3);while(s--){shadow="0 "+h+"em "+ -h+"em "+colors[c];textShadows.push(shadow);h-=hv}
c++}
$(this).css({color:colors[0],textShadow:textShadows.join(", ")})}
$(begin);function begin(){$("div.slogan").ignite(gasFlame)}